import CardText from './cardText/cardText';
import CardImage from './cardImage'
import imagem from '../../images';
import CardButton from './cardButton';
import changes from '../../images/fluxograma.json';

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
        pageId : 0, //initial
    }
  }
  render() {
    console.log(imagem.gif16);
    return (
      <div className="principalCard">
        <div>
          <CardImage src={changes[this.state.pageId].imagem} />
          <CardText name={changes[this.state.pageId].pergunta}/>
        </div>
        <div className="items-center flex flex-column">
          <div className="btn-desktop">

          <div className="btnbutton">
            <CardButton isPrimary className="btnbutton">Sim</CardButton>
          </div>
          <div className="btnbuttonNao">
            <CardButton className="btnbuttonNao">NÃ£o</CardButton>
          </div>
          </div>
          <CardButton link />
        </div>
      </div>
    );
  }
}

export default Card;
