    return (
    //<button className={cls}>{children}</button>
    {children}
   );

};

const PrimaryLink = () => {
    return(
        <a href="#" className="button-font-size outline-0 text-color-blue link-decoration-none button-border-bottom">Recome√ßar Quiz</a>
    );
};

const CardButton = props => {
    const ButtonComp = props.link ? PrimaryLink : PrimaryButton;
    return <ButtonComp {...props}/>
  };

export default CardButton;
