import React, { Component } from 'react';
import changes from '../../../images/fluxograma.json';
import {findObjById} from '../../../utils';


const nao = 'Não';
const sim = 'Sim';




const ClickVerif = (children) => {
    switch (children) {
        case nao:
            this.setState({pageId: changes[findObjById(this.state.pageId)].nao})
            break;

        case sim:
        this.setState({pageId: changes[findObjById(this.state.pageId)].nao})
            break;

        drefault:
        this.setState({pageId: changes[0]})
            break;
    }
}

const PrimaryButton = ({isPrimary, children}) => {
    const cls = isPrimary ? 'bg-buttony white br24 button-height-mobile button-width-mobile button-noborder button-font-size button-margin-top button-margin-bottom-primary' : '.text-color-blue br24 bg-button-transp button-height-mobile button-width-mobile button-border button-font-size button-margin-bottom-secundary';
    return (
    <button className={cls} onClick={() => ClickVerif(children)}>{children}</button>
   );

};

const PrimaryLink = () => {
    return(
        <a href="#" className="button-font-size outline-0 text-color-blue link-decoration-none button-border-bottom">Recomeçar Quiz</a>
    );
};

const CardButton = props => {
    const ButtonComp = props.link ? PrimaryLink : PrimaryButton;
    return <ButtonComp {...props}/>
  };

export default CardButton;
